import React, { useState } from "react";
import { createResident } from "../../service/Resident/apiResidentCreate";

const Fill_Info = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    dateOfBirth: "",
    gender: "",
    idNumber: "",
    passportNumber: "",
    issueDate: "",
    expiryDate: "",
    phone: "",
    email: "",
    address: "",
    serviceType: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Gọi API với dữ liệu form
      const response = await createResident(formData);

      // Kiểm tra phản hồi từ server
      if (response) {
        alert("Gửi thông tin thành công!");
        // Reset form (nếu cần)
        setFormData({
          fullName: "",
          dateOfBirth: "",
          gender: "",
          idNumber: "",
          passportNumber: "",
          issueDate: "",
          expiryDate: "",
          phone: "",
          email: "",
          address: "",
          serviceType: "",
        });
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Không thể kết nối với server. Vui lòng thử lại sau.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-blue-200 py-6 sm:py-8 md:py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl sm:text-3xl md:text-4xl font-bold text-center mb-6 sm:mb-8 md:mb-12 text-blue-800 animate-fade-in">
          Điền Thông Tin Gia Hạn Hộ Chiếu
        </h1>

        <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6 md:p-8">
          <form onSubmit={handleSubmit} className="space-y-4 sm:space-y-6">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 sm:gap-6">
              <div className="group">
                <label className="block text-gray-700 font-semibold mb-1.5 sm:mb-2">
                  Họ và tên
                </label>
                <input
                  type="text"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  className="w-full px-3 sm:px-4 py-2 sm:py-3 border-2 rounded-lg focus:outline-none focus:border-blue-500 bg-gray-50 group-hover:border-blue-300 transition-all"
                  required
                />
              </div>
              {/* Thêm các input khác tương tự */}
            </div>

            <div className="flex justify-center mt-6 sm:mt-8">
              <button
                type="submit"
                className="w-full sm:w-auto bg-blue-600 text-white px-6 sm:px-8 py-2.5 sm:py-3 rounded-lg font-semibold hover:bg-blue-700 transition duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
              >
                Gửi Thông Tin
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Fill_Info;
